import axios from "axios";
import { useToast } from "vue-toastification";
export default {
  name: "activation",
  props: ['uid', 'slug'],
  data: () => ({
    loading: false,
    toast: useToast()
  }),
  methods: {
    goTo() {
      this.$router.replace({
        name: 'home'
      });
    },
    activate() {
      const formData = new FormData();
      formData.append("uid", this.uid);
      formData.append("token", "JWT" + this.slug);
      this.loading = true, axios({
        url: this.$store.state.backendUrl + `auth/users/activation/`,
        data: formData,
        method: 'POST',
        headers: {
          'Content-type': 'application/json'
        }
      }).then(response => {
        this.loading = false;
        this.toast.success('Аккаунт успешно подтвержден');
        let r = this.$router.resolve({
          name: 'home' // put your route information in
        });

        window.location.assign(r.href);
      }).catch(err => {
        this.toast.error('Ошибка. Ваш токен устарел'), this.loading = false;
      });
    }
  }
};
